DELIMITER $$
CREATE TRIGGER `update_wallet_balance` AFTER INSERT ON `ledger` FOR EACH ROW BEGIN 
  UPDATE wallets 
  SET current_balance = (
    SELECT COALESCE(SUM(
      CASE 
        WHEN tx_type = 'credit' THEN amount 
        ELSE -amount 
      END
    ), 0) + opening_balance
    FROM ledger 
    WHERE wallet_id = NEW.wallet_id 
      AND (deleted_at IS NULL OR deleted_at = '')
      AND status = 'approved'
  ),
  last_transaction_at = NOW()
  WHERE id = NEW.wallet_id;
END
$$
DELIMITER ;

