CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `finance_summary`  AS SELECT `w`.`school_id` AS `school_id`, `w`.`id` AS `wallet_id`, `w`.`name` AS `wallet_name`, `w`.`currency` AS `currency`, `w`.`opening_balance` AS `opening_balance`, `w`.`current_balance` AS `current_balance`, coalesce(`income`.`total`,0) AS `total_income`, coalesce(`expenses`.`total`,0) AS `total_expenses`, cast(current_timestamp() as date) AS `summary_date` FROM ((`wallets` `w` left join (select `ledger`.`wallet_id` AS `wallet_id`,sum(`ledger`.`amount`) AS `total` from `ledger` where `ledger`.`tx_type` = 'credit' group by `ledger`.`wallet_id`) `income` on(`w`.`id` = `income`.`wallet_id`)) left join (select `ledger`.`wallet_id` AS `wallet_id`,sum(`ledger`.`amount`) AS `total` from `ledger` where `ledger`.`tx_type` = 'debit' group by `ledger`.`wallet_id`) `expenses` on(`w`.`id` = `expenses`.`wallet_id`)) WHERE `w`.`is_active` = 1 ;

