CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `security_audit_view`  AS SELECT `al`.`id` AS `id`, `al`.`school_id` AS `school_id`, `al`.`user_id` AS `user_id`, `u`.`email` AS `email`, `u`.`username` AS `username`, `al`.`action` AS `action`, `al`.`ip_address` AS `ip_address`, `al`.`status` AS `status`, `al`.`details` AS `details`, `u`.`full_name` AS `full_name`, `u`.`role_name` AS `role_name`, `al`.`created_at` AS `created_at` FROM (`auth_logs` `al` left join `active_users_view` `u` on(`al`.`user_id` = `u`.`id`)) ORDER BY `al`.`created_at` DESC ;

