--
-- Structure for view `teacher_daily_schedule`
--
DROP TABLE IF EXISTS `teacher_daily_schedule`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `teacher_daily_schedule`  AS SELECT `t`.`id` AS `id`, `t`.`school_id` AS `school_id`, `t`.`teacher_id` AS `teacher_id`, concat(`p`.`first_name`,' ',`p`.`last_name`) AS `teacher_name`, `t`.`lesson_date` AS `lesson_date`, `t`.`start_time` AS `start_time`, `t`.`end_time` AS `end_time`, `t`.`room` AS `room`, `c`.`name` AS `class_name`, `s`.`name` AS `subject_name`, `t`.`status` AS `status`, `t`.`lesson_title` AS `lesson_title`, `t`.`lesson_type` AS `lesson_type`, `t`.`attendance_taken` AS `attendance_taken`, CASE WHEN `t`.`lesson_date` = curdate() AND `t`.`start_time` <= curtime() AND `t`.`end_time` >= curtime() THEN 'ongoing' WHEN `t`.`lesson_date` = curdate() AND `t`.`start_time` > curtime() THEN 'upcoming' WHEN `t`.`lesson_date` = curdate() AND `t`.`end_time` < curtime() THEN 'past' ELSE 'scheduled' END AS `lesson_status` FROM ((((`timetable` `t` join `staff` `st` on(`t`.`teacher_id` = `st`.`id`)) join `people` `p` on(`st`.`person_id` = `p`.`id`)) join `classes` `c` on(`t`.`class_id` = `c`.`id`)) join `subjects` `s` on(`t`.`subject_id` = `s`.`id`)) WHERE `t`.`deleted_at` is null AND `t`.`status` in ('scheduled','ongoing','completed') ;
